	{

    foreach (sfMixer::getCallables('BaseWorkerPeer:doSelectJoin:doSelectJoin') as $callable)
    {
      call_user_func($callable, 'BaseWorkerPeer', $c, $con);
    }


		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		WorkerPeer::addSelectColumns($c);
		$startcol = (WorkerPeer::NUM_COLUMNS - WorkerPeer::NUM_LAZY_LOAD_COLUMNS);
		CitizenshipPeer::addSelectColumns($c);

		$c->addJoin(array(WorkerPeer::CITIZENSHIP_ID,), array(CitizenshipPeer::ID,), $join_behavior);
		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = WorkerPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = WorkerPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {

